package r2;

public class Precedence {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double first;
		double second;
		double average;
		first=16;
		second=4;
		average=first+second/2;
		System.out.println(average);
		average=(first+second)/2;
		System.out.println(average);
	}

}
